import jenkins.model.*
import hudson.model.*

def applicationID = build.getEnvironment(listener).get( 'applicationID' ) 
def idTecnologia = build.getEnvironment(listener).get( 'idTecnologia' ) 
def tipoRepository = build.getEnvironment(listener).get( 'tipoRepository' ) 
def ultimoTagoTrunk = build.getEnvironment(listener).get( 'ultimoTagoTrunk' ) 
def pathSorgente = build.getEnvironment(listener).get( 'pathSorgente' ) 
def descrizioneApp = build.getEnvironment(listener).get( 'descrizioneApp' ) 
def descrizione = build.getEnvironment(listener).get( 'descrizione' ) 
def dettagliTecnologici = build.getEnvironment(listener).get( 'dettagliTecnologici' ) 

def analisi= build.getEnvironment(listener).get( 'analisi' ) 

def URL = pathSorgente.replace('<<TTLT>>','trunk')
println URL


def job = Hudson.instance.getJob('A40_SVN_download_trunk')

    def params = [
      new StringParameterValue('applicationID', applicationID ),
      new StringParameterValue('idTecnologia', idTecnologia),
      new StringParameterValue('pathSorgente', URL),    
      new StringParameterValue('descrizioneApp', descrizioneApp),    
      new StringParameterValue('analisi', analisi),    
    ]
    def future = job.scheduleBuild2(0,  new ParametersAction(params))

println "Lanciato job A40_SVN_download_trunk per applicazione "+applicationID +" Tecnologia "+idTecnologia+" descrizioneApp "+descrizioneApp+" analisi "+analisi

