import groovy.util.XmlSlurper
import hudson.model.StringParameterValue
import hudson.util.*
import jenkins.model.*
import hudson.model.*

def config = new HashMap()
def bindings = getBinding()
config.putAll(bindings.getVariables())
def out = config['out']
// specified variables (in Jenkins job)
def thr = Thread.currentThread()
def build = thr?.executable
def buildMap = build.getBuildVariables()
config.putAll(buildMap)
// build/environmental variables
def envVarsMap = build.parent.builds[0].properties.get("envVars")

config.putAll(envVarsMap)

out.println "-" * 80
def BUILD_ID = config.get('BUILD_ID')
def WORKSPACE = config.get('WORKSPACE')
def TAGS_BRANCHES = config.get('DEFAULT_TAGS_OR_BRANCHES')

def parametri = [: ]
def parms = new File(WORKSPACE + "/prm_" + BUILD_ID + ".txt")
parms.eachLine() {     line ->
        r = line.split("=")
    parametri.put(r[0], r[1])
}
parametri.each() {     k,  v ->
    println "k:" + k + "......v:" + v
}



def tagsXml = new File(parametri['tagsXml'])

def contenuto = tagsXml.text
def rootNode = new XmlSlurper().parseText(contenuto)
def nomeScelto = ""
def dataScelta = Date.parse("yyyy-MM-dd hh:mm:ss", "1900-01-01 00:00:00")
rootNode.list.entry.each() {  lista ->
        name = lista['name']
    def data = Date.parse("yyyy-MM-dd hh:mm:ss", "1900-01-01 00:00:00")
    lista.commit[0].each() {   commits ->
            println "+" * 80
        println commits['author']
        println name.toString()
        d = commits['date'].toString()
        println "+" * 80
        n = d.indexOf(".")

        def dt = d[0..18].replace("T", " ")
        println dt

        Date dataLetta = Date.parse("yyyy-MM-dd hh:mm:ss", dt)
        println dataLetta
        println "+" * 80
        if (dataLetta > data) {
            data = dataLetta
        }

    }
    if (data >= dataScelta) {
        dataScelta = data
        nomeScelto = name.toString()
    }
}
println "Scarico il tag " + nomeScelto + " con ultima commit " + dataScelta
def nuovoPath="tags/"+nomeScelto
def svnURL = parametri['pathSorgente'].replace('<<TTLT>>', nuovoPath)
def applicationID=parametri['applicationID']
def idTecnologia=parametri['idTecnologia']
def pathSorgente=parametri['pathSorgente']
def descrizioneApp=parametri['descrizioneApp']
def analisi=parametri['analisi']

def job = Hudson.instance.getJob('A20_SVN_download_tags')

    def params = [
      new StringParameterValue('applicationID', applicationID ),
      new StringParameterValue('idTecnologia', idTecnologia),
      new StringParameterValue('pathSorgente', svnURL ),    
      new StringParameterValue('descrizioneApp', descrizioneApp),    
      new StringParameterValue('TAG', nomeScelto),    
      new StringParameterValue('analisi', analisi),    
    ]
    def future = job.scheduleBuild2(0,  new ParametersAction(params))

println "Lanciato job SVN_download_tags per applicazione "+applicationID+" Tecnologia "+idTecnologia
